tool.command:
bcftools concat -O v \
-o out.tmp.vcf \
-a \
${sv_vcf} ${cnv_vcf};

cat out.tmp.vcf | grep "^##" > header.tmp.vcf;
cat out.tmp.vcf | grep "^#CHROM" >> header.tmp.vcf;

java -jar picard.jar \
SortVcf \
I=out.tmp.vcf \
O=out.sort.vcf \
SEQUENCE_DICTIONARY=reference.dict ;

java -jar picard.jar \
FixVcfHeader \
I=out.sort.vcf \
O=${sv_cnv_concat} \
H=header.tmp.vcf ;
